listeners.selectionchanged:
function(comp, val, isChecked) { 
	var dlg = comp.findParentByType('dialog');
	var promo = dlg.getField('./promoPath');

	if (!isChecked) {
		promo.disable();		
	}
	else{
		promo.enable();	
	}
}

listeners.beforesubmit:
function(dlg){
	var isPopup = dlg.getField('./signinPopup').getValue();
    var promo = dlg.getField('./promoPath').getValue();
	var submit = true;	

	if(isEmpty(isPopup)){
		if(promo.trim() === ''){
			CQ.Ext.Msg.alert('Mandatory Field', 'Promo Path should not be blank.');
			submit = false;
		}		
	}

	return submit;
}

function isEmpty(val){
    return (val === undefined || val == null || val.length <= 0) ? false : true;
}
