listeners.beforesubmit:
function(dialog) {
    var number = dialog.getField('./itemCount').getValue();
	if(number) {



    if (number > 7) {
        CQ.Ext.Msg.show({
            title: 'Realted Products Validation',
            msg: 'Value Greater than 7 is not allowed',
            icon: CQ.Ext.MessageBox.WARNING,
            buttons: CQ.Ext.Msg.OK
        });
        return false;
    }
    if (number < 4) {
        CQ.Ext.Msg.show({
            title: 'Realted Products Validation',
            msg: 'Value Less than 4 is not allowed',
            icon: CQ.Ext.MessageBox.WARNING,
            buttons: CQ.Ext.Msg.OK
        });
        return false;
    }
	}
    return true;
}
