listeners.beforesubmit:
function(dialog)
{ 
var img = dialog.getField('./image/file').hasData(); 
var imgPath = dialog.getField('./externalImagePath').getValue();
if(img && imgPath.length>0)
{CQ.Ext.Msg.show({'title':'Dialog Validation','msg':'Please provide Image either from Image Tab or using HTTP Path of Image path.','buttons':CQ.Ext.Msg.OK,'icon':CQ.Ext.MessageBox.INFO,'scope':this}); 
return false;
} 

var imageLink = dialog.getField('./imageLink').getValue();
var thumb = dialog.getField('./createThumbnail').getValue();
var uploadLink = dialog.getField('./file').hasData();
if(thumb == "")
{
thumb = false;
}
if((thumb && uploadLink && imageLink.length>0) || (thumb && uploadLink) || (thumb && imageLink.length>0) || (uploadLink && imageLink.length>0))
{CQ.Ext.Msg.show({'title':'Dialog Validation','msg':'Please provide Only Thumbnail Or Image Link Or Upload a file in Advanced Image Tab.','buttons':CQ.Ext.Msg.OK,'icon':CQ.Ext.MessageBox.INFO,'scope':this}); 
return false;
} 
}
