}

categoryCode.optionsProvider:
//Categories - Option Provider
function(path, rec) {
  var filtersJSON = ' ';
  var categoryJSON = [];
  $.ajax({
    url: '/apps/mcd-us/components/content/coke-store-locator/coke-store-filters/coke-store-filters.json',
    dataType: 'json ',
    type: 'GET',
    async: false,
    success: function(data) {
      filtersJSON = data.beverageCategory;
    }
  }); //Parsing Local Json  
  $.each(filtersJSON, function(key, item) {
    var obj = {
      value: item.code,
      text: item.name
    }
    categoryJSON.push(obj);
  });
  this.addClass('catCount');
  console.log('Categories - Option Provider', categoryJSON);
  return categoryJSON;
}

